import React, { useState } from 'react';
import { Link, useRouteMatch } from 'react-router-dom';
import { Box, AppBar, Toolbar, Container, Avatar, Typography, Button, Menu, MenuItem } from '@mui/material';

const pages = [
 {label: 'Home', id: '/'},
 {label: 'Menu', id: 'menu'}, 
 {label: 'Reservation', id: 'reservation'},
 {label:'Contact', id: 'footer' },
 {
    label: 'My Account', 
    id: 'my-account',
    handler: (event, setAnchorEl) => {
      setAnchorEl(event.currentTarget);
    }
  }
];
const credentials = ['Staff Login', 'Customer Login'];

const NavBar = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const match = useRouteMatch(); // Use useRouteMatch hook

  // determines the action to take when a page is clicked. 
  // If the page has a 'handler', it calls the handler, otherwise,
  // it scrolls to the corresponding section
  const handleNavClick = (id, event) => {
    event.preventDefault(); // Prevent the default link behavior
    const page = pages.find((page) => page.id === id);
    if (page && page.handler) {
      page.handler(event, setAnchorEl);
    } else {
      const element = document.getElementById(id);
      if (element) {
        if (match.path === '/') { // Check if the current route matches the homepage
          element.scrollIntoView({ behavior: 'smooth' });
        } else {
          window.location.href = '/'; // Navigate to the homepage
          setTimeout(() => {
            const homeElement = document.getElementById(id);
            if (homeElement) {
              homeElement.scrollIntoView({ behavior: 'smooth' });
            }
          }, 100); // Wait for the page transition to complete
        }
      }
    }
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const myAccountPage = pages.find(page => page.label === 'My Account');

  return (
    <AppBar position="static" sx={{ backgroundColor: 'transparent'}}>
      <Container maxWidth="xl">
        <Toolbar>
          <Link to="/" style={{ textDecoration : 'none'}}>
            <Avatar 
                alt="Logo" 
                src={`logo.png`} 
                sx={{ width: '6rem', paddingTop: '1rem'}}
            />
            <Typography
              sx={{ 
                color: 'black',
                fontSize: '0.7rem',
                fontFamily: 'Knewave',
                paddingBottom: '1rem'
              }}
            >
              RELAXING KOALA
            </Typography>
          </Link>
          <Box sx={{ flexGrow: 1, display: "flex" }} />
            {pages.map((page) => (
            <Link 
               key={page.label}
               to={page.id}
               style={{ marginRight: '1rem', cursor: 'pointer', textDecoration: 'none', color:'black', fontWeight: '600' }}
               onClick={(event) => handleNavClick(page.id, event)}
              >
               {page.label}
            </Link>
            ))}
        </Toolbar>
      </Container>
      <Menu
      id="login-details"
      anchorEl={anchorEl}
      keepMounted open={Boolean(anchorEl)}
      onClose={handleClose}
      >
        {credentials.map((credential, index) =>(
          <MenuItem key={index} onClick={handleClose}>{credential}</MenuItem>
        ))}
      </Menu>
    </AppBar>
  );
}

export default NavBar;
