import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Box, AppBar, Toolbar, Container, Avatar, Typography, Button, Menu, MenuItem } from '@mui/material';

const pages = [
  { label: 'Home', id: '/' },
  { label: 'Menu', id: 'menu' },
  { label: 'Reservation', id: 'reservation' },
  { label: 'Contact', id: 'footer' },
  {
    label: 'My Account',
    id: 'my-account',
    handler: (event, setAnchorEl) => {
      setAnchorEl(event.currentTarget);
    },
  }
];

const credentials = ['Staff Login', 'Customer Login'];

const NavBar = () => {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleNavClick = (id) => {
    const page = pages.find((page) => page.id === id);
    if (page && page.handler) {
      page.handler(null, setAnchorEl);
    } else {
      document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <AppBar position="fixed" sx={{ backgroundColor: 'transparent' }}>
      <Container maxWidth="xl">
        <Toolbar>
          <Link to="/" style={{ textDecoration: 'none' }}>
            <Avatar 
              alt="Logo" 
              src={`logo.png`} 
              sx={{ width: '6rem', paddingTop: '1rem' }}
            />
            <Typography
              sx={{ 
                color: 'black',
                fontSize: '0.7rem',
                fontFamily: 'Knewave',
                paddingBottom: '1rem'
              }}
            >
              RELAXING KOALA
            </Typography>
          </Link>
          <Box sx={{ flexGrow: 1, display: "flex" }} />
          {pages.map((page) => (
            <Link
              key={page.label}
              to={page.id}
              spy={true}
              smooth={true}
              offset={50}
              duration={500}
              style={{ marginRight: '1rem', cursor: 'pointer', textDecoration: 'none', color: 'black', fontWeight: '600' }}
              onClick={() => handleNavClick(page.id)}
            >
              {page.label}
            </Link>
          ))}
        </Toolbar>
      </Container>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        {credentials.map((credential, index) => (
          <MenuItem key={index} onClick={handleClose}>{credential}</MenuItem>
        ))}
      </Menu>
    </AppBar>
  );
};

export default NavBar;
