import React from 'react';
import { Link } from 'react-router-dom';

const pages = ['Home', 'Menu', 'Reservation', 'Contact', 'My Account'];

const Homepage = () => {
  return (
    <React.Fragment>
      <img 
        src="/image1.jpg" alt="Description of the image" 
        style={{ width: '100%', height: 'auto', opacity: 0.7 }} 
      />
      <div 
        style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundColor: 'rgba(0, 0, 0, 0.5)', // Adjust the opacity here (0.5 = 50%)
        }}
      />
    </React.Fragment>
  );
}

export default Homepage;
