import React from 'react';
import { Link } from 'react-router-dom';
import { Box, AppBar, Toolbar, Container, Avatar, Typography, Menu, MenuItem } from '@mui/material';

const pages = ['Home', 'Menu', 'Reservation', 'Contact', 'My Account']
const NavBar = () => {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: 'transparent'}}>
      <Container maxWidth="xl">
        <Toolbar>
          <Link to="/" style={{ textDecoration : 'none'}}>
            <Avatar 
                alt="Logo" 
                src={`logo.png`} 
                sx={{ width: '6rem', paddingTop: '1rem'}}
            />
            <Typography
            sx={{ 
                color: 'black',
                fontSize: '0.7rem',
                fontFamily: 'Knewave',
            }}
            >
                RELAXING KOALA
            </Typography>
          </Link>
          <Box>
            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleClose}
            >
                {pages.map((page)=>(
                    <MenuItem key={page} onClick={handleClose}>

                          <Typography textAlign="center" color="black">{page}</Typography>

                    </MenuItem>
                ))}
            </Menu>
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}

export default NavBar;
