import React, { useState } from 'react'; // Import useState hook
import { Typography, Paper, TextField, InputLabel, Button } from '@mui/material';

const pages = ['Home', 'Menu', 'Reservation', 'Contact', 'My Account'];

const Homepage = () => {
    const [formData, setFormData] = useState({
      name: '',
      date: '',
      time: '',
      numbOfPeople: '',
      specialRequests: ''
    });
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({
        ...formData,
        [name]: value
      });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      // You can perform any actions you need here, such as sending the form data to a server
      console.log('Form submitted:', formData);
    };
  
  return (
    <div style={{ position: 'relative', width: '100%', height: 'auto' }}>
      <img 
        src="/image1.jpg" alt="Description of the image" 
        style={{ width: '100%', height: 'auto' }} 
      />
      <div 
        style={{
          position: 'absolute',
          top: '10%',
          width: '100%',
          height: '400px',
          backgroundColor: 'rgba(255, 255, 255, 0.6)'
        }}
      >
        <Typography variant="h3" align="left" color="#000000" sx={{ paddingTop: '8rem', paddingLeft: '2rem', fontWeight: 'bold'}}>
          WELCOME TO RELAXING KOALA 
        </Typography>
        <Typography variant="h3" align="left" color="#000000" sx={{ paddingTop: '1rem', paddingLeft: '2rem', fontWeight: 'bold'}}>
          - WHERE FLAVOURS MEET FRIENDS
        </Typography>
      </div>
      <div>
        <Typography variant="h4" sx={{ fontWeight: 'bold', padding:'2rem'}}>MENU</Typography>
      </div>
      <img 
        src="/Menu.jpg" alt="Description of the image" 
        style={{ width: '100%', height: 'auto' }} 
      />
      <Typography variant="h4" sx={{ fontWeight: 'bold', padding:'2rem'}}>BOOK A TABLE</Typography>
      <div>
        <form style={{ display: 'flex', flexDirection: 'column', gap: '1rem', alignItems: 'center'}} onSubmit={handleSubmit}>
          <TextField 
            label="Name"
            variant="outlined" 
            name="name" 
            InputLabelProps={{
              shrink: true,
            }}
            value={formData.name} 
            onChange={handleChange} 
            sx={{ width: '500px' }} 
            required 
          />

          <TextField 
            label="Date"
            variant="outlined" 
            name="date" 
            type="date"
            InputLabelProps={{
              shrink: true,
            }}
            value={formData.date} 
            onChange={handleChange} 
            sx={{ width: '500px' }} 
            required 
          />

          <TextField 
            label="Time"
            type="time"
            variant="outlined" 
            name="time" 
            InputLabelProps={{
              shrink: true,
            }}
            value={formData.time} 
            onChange={handleChange} 
            sx={{ width: '500px' }} 
            required 
          />

          <TextField 
            label="Number of People"
            variant="outlined" 
            name="numbOfPeople" 
            InputLabelProps={{
              shrink: true,
            }}
            value={formData.numbOfPeople} 
            onChange={handleChange} 
            sx={{ width: '500px' }} 
            required 
          />

          <TextField 
            label="Special Requests"
            variant="outlined" 
            name="specialRequests" 
            InputLabelProps={{
              shrink: true,
            }}
            value={formData.specialRequests} 
            onChange={handleChange} 
            sx={{ width: '500px' }} 
            required 
          />
          <Button type="submit" variant="contained" sx={{ width: '500px', backgroundColor: '#3D9D9D9'}}>Submit</Button>
        </form>
      </div>
    </div>
  );
}

export default Homepage;
